using UnityEngine;
using UnityEngine.EventSystems;

namespace Microsoft.MixedReality.Toolkit.Core.EventDatum.Input
{
    /// <summary>
    /// Describes a uGUI event that was generated by a specific pointer.
    /// </summary>
    public class GraphicInputEventData : PointerEventData
    {
        /// <inheritdoc />
        public GraphicInputEventData(EventSystem eventSystem) : base(eventSystem) { }

        /// <summary>
        /// Clears the pointer data and calls the base <see cref="PointerEventData"/>'s Reset method.
        /// </summary>
        public void Clear()
        {
            Reset();
            button = InputButton.Left;
            clickCount = 0;
            clickTime = 0;
            delta = Vector2.zero;
            dragging = false;
            eligibleForClick = false;
            pointerCurrentRaycast = default(RaycastResult);
            pointerDrag = null;
            pointerEnter = null;
            pointerId = 0;
            pointerPress = null;
            pointerPressRaycast = default(RaycastResult);
            position = Vector2.zero;
            pressPosition = Vector2.zero;
            rawPointerPress = null;
            scrollDelta = Vector2.zero;
            selectedObject = null;
            useDragThreshold = false;
        }
    }
}